<?php

class indexControl extends Control {

    function index() {
        $this->display();
    }

    //城市导航
    function citymap()
    {
        $db=M('city');
        $citys=$db->cache(86400*10)->where('is_open=1')->order('ucfirst')->findall();
        $citys_fmt=array();
        foreach ($citys as $value) {
            $citys_fmt[$value['ucfirst']][]=$value;
        }
        $this->assign('citys_format',$citys_fmt);
        $this->display('citymap');
    }

    //城市专栏招聘
    function cityColumn()
    {
        $db=M('city');
        $cond=array(
            'pinyin'=>$_GET['name'],
            'is_open'=>1//如果开启了主站
        );
        $city=$db->field('id,name')->where($cond)->find();
        if(!$city){
            $this->error('对不起，未找到城市信息。','index');
        }
        $this->assign('city',$city);
        $this->display('city');
    }
    //企业主页
    function company() {
        if (!isset($_GET['id']) or $_GET['id']==1) {
            $this->error('没有该企业的信息');
        }
        $uid = intval($_GET['id']);//企业ID
        $db = M('company_info');
        $company = $db->where('uid=' . $uid)->find();
        if (!$company) {
            $this->error('没有该企业的信息');
        }
        $cond = array('uid' =>$uid ,'expiration_time>'.time(),'verify=1','state=1');
        $recruits = $db->table('recruit')->where($cond)->field('recruit_id,start_time,recruit_name')->findall();
        $data=new data('recruit');
        $company=$data->convert($company);
        $this->assign('company', $company);
        $this->assign('recruits',$recruits);
        if(empty($company['tpl_style'])){
            $company['tpl_style']='skyblue';//默认风格
        }
        if(!empty($_GET['style']) && $uid==$_SESSION['uid']){//企业预览模板
            $company['tpl_style'] = $_GET['style'];
        }
        $this->display(PATH_ROOT . '/templates/company_tpl/' . $company['tpl_style'] . '/index');
    }

    //意见反馈
    function feedback(){
        if($_SERVER['REQUEST_METHOD']=='POST'){
            $_POST['content']=htmlspecialchars($_POST['content']);
            $_POST['title']=strip_tags($_POST['title']);
            $_POST['name']=strip_tags($_POST['name']);
            $_POST['email']=strip_tags($_POST['email']);
            $_POST['type']=intval($_POST['type']);
            $_POST['created']=time();
            $db=M('feedback');
            if($db->insert()){
                $this->success('反馈成功！','index');
            }
        }
        $type=array(
            1=>'建议',
            2=>'咨询',
            3=>'举报',
            4=>'求助'
        );
        if(!isset($_GET['type'])){
            $_GET['type']=1;
        }
        $this->assign('type',$type);
        $this->display();
    }

    public function class2(){

        $classs = M('linkage')->field('laid','title')->where(array('pid'=>0,'lcgid'=>4))->order('sort asc')->findall();
        foreach($classs as $key=>$class){
            $classTwo = M('linkage')->field('laid','title')->where(array('pid'=>$class['laid'],'lcgid'=>4))->order('sort asc')->findall();

            foreach($classTwo as $k=>$class2){

                $class3 = M('linkage')->field('laid','title')->where(array('pid'=>$class2['laid'],'lcgid'=>4))->order('sort asc')->findall();
                $class2['class3'] = $class3;
                $classs[$key]['class2'][$k/3][]=$class2;
            }

        }

        $this->assign('classs',$classs);



        $content = $this->display('class2',0,'text/html','utf-8',false);

        $path=PATH_ROOT.'/html/class';
        if(!file_exists($path)){
            dir_create($path);
        }
        $path.='/class.html';

        echo file_put_contents($path, $content);
    }



    public function ios(){
        $this->display();
    }

    public function resize(){
        var_dump($_GET);
    }


